type ExtendedIngredient = {
  aisle: string;
  amount: number;
  consitency: string;
  id: number;
  image: string;
  measures: {
    metric: {
      amount: number;
      unitLong: string;
      unitShort: string;
    };
    us: {
      amount: number;
      unitLong: string;
      unitShort: string;
    };
  };
  meta: string[];
  name: string;
  original: string;
  originalName: string;
  unit: string;
};

type AnalyzedInstruction = {
  name: string;
  steps: RecipeStep[];
};

type RecipeStep = {
  number: number;
  step: string;
  ingredients: RecipeIngredient[];
  equipment: RecipeEquipment[];
  length?: {
    number: number;
    unit: string;
  };
};

type RecipeIngredient = {
  id: number;
  name: string;
  localizedName: string;
  image: string;
};

type RecipeEquipment = {
  id: number;
  name: string;
  localizedName: string;
  image: string;
};
